mapper.select:
SELECT wiz_bbs.idx,prdcode,tidx,leccode,wiz_bbs.code,wiz_bbscat.catname,prino, grpno,depno,star,memid,memgrp,name,email,subject,content FROM wiz_bbs LEFT OUTER JOIN wiz_bbscat ON wiz_bbs.code = wiz_bbscat.code
where wiz_bbs.code = #{code2}
AND subject LIKE CONCAT('%',#{title},'%')
AND memid LIKE CONCAT('',#{userid},'')
AND wdate between #{wdate1} and #{wdate2}
AND content LIKE CONCAT('',#{memo1},'')
ORDER BY idx desc LIMIT ${rowStart-1}, 10;

SELECT wiz_bbs.idx,prdcode,tidx,leccode,wiz_bbs.code,wiz_bbscat.catname,prino, grpno,depno,star,memid,memgrp,NAME,email,subject,content FROM wiz_bbs LEFT OUTER JOIN wiz_bbscat ON wiz_bbs.code = wiz_bbscat.code
where leccode = #{courseid}
AND subject LIKE CONCAT('%',#{title},'%')
AND memid LIKE CONCAT('',#{userid},'')
AND wdate between #{wdate1} and #{wdate2}
AND content LIKE CONCAT('',#{memo1},'')
ORDER BY prino desc LIMIT ${rowStart-1}, 10;

select count(*) from wiz_bbs where leccode = #{courseid}
AND subject LIKE CONCAT('%',#{title},'%')
AND memid LIKE CONCAT('',#{userid},'')
AND wdate between #{wdate1} and #{wdate2}
AND content LIKE CONCAT('',#{memo1},'')

select count(*) from wiz_bbs where code = #{code2}
AND subject LIKE CONCAT('%',#{title},'%')
AND memid LIKE CONCAT('',#{userid},'')
AND wdate between #{wdate1} and #{wdate2}
AND content LIKE CONCAT('',#{memo1},'')

